client.on('message', async message => {
  
  let prefix = ayarlar.prefix
  
  let kullanıcı = message.mentions.users.first() || message.author
  let afkdkullanıcı = await db.fetch(`afk_${message.author.id}`)
  let afkkullanıcı = await db.fetch(`afk_${kullanıcı.id}`)
  let sebep = afkkullanıcı
 
  if (message.author.bot) return;
  if (message.content.includes(`${prefix}afk`)) return;
  
  if (message.content.includes(`<@${kullanıcı.id}>`)) {
    if (afkdkullanıcı) {
      message.channel.send(`:rocket: **| \`${message.author.tag}\` Adlı Kullanıcı Artık AFK Değil. Hoşgeldin ${message.author.tag}**`)
      db.delete(`afk_${message.author.id}`)
    }
    if (afkkullanıcı) return message.channel.send(`${message.author}\`${kullanıcı.tag}\` **ŞUAN Afk. Sebep:** \`${sebep}\``)
  }

  if (!message.content.includes(`<@${kullanıcı.id}>`)) {
    if (afkdkullanıcı) {
      message.channel.send(`:rocket: **| \`${message.author.tag}\` Adlı Kullanıcı Artık AFK Değil. Hoşgeldin** <@` + message.author.id +`>`)
      db.delete(`afk_${message.author.id}`)
    }
  }
});





